import TopRated from "./TopRated"
import MovieGrid from "./components/MovieGrid";

function HomePage() {
    const [arrays, setArray] = useState([]);
    useEffect( async ()=> {
    const response = await fetch("/main");
    const data = await response.json();
    console.log(data);
    setArray(data.data); //request tek object olarak geliyor(data[]) useState array olarak atanamıyor
  }, [])
    return (
        <>
        <Header/>
        <MovieGrid data1={data}/>

        HomePage
        </>
    )
}
