import React from 'react'

export default function SearchCard(props) {
    const runCallback = (cb) => {
        return cb();
      };
    let division = 3;
  return (
    <div class="flex bg-slate-400 justify-center">
        <div class="flex-col w-5/6 pt-6 md:w-5/6">
            {props.data.data.map(data =>
            <div class="flex flex-col md:flex-row gap-2 mb-6 p-4 rounded-xl text-black ">
                <img class="rounded-2xl w-40 h-60" src={"https://www.themoviedb.org/t/p/w300_and_h450_bestv2" + data.poster_path} alt={data.title} /> 
                <div class="p-3 ml-6 md:p-1">
                    <div>
                        <a class="text-2xl" href={"/movies/" + data.id + data.title}> {data.title} </a>
                    </div>
                    <div class="flex text-center pt-8 justify-start">
                        <div class="">
                            <p> {data.vote_average} <i class="fa-solid fa-signal"></i></p>
                            <div>
                                {
                                runCallback(() => {
                                {division = Math.floor(data.vote_average)/2}
                                const row = [];
                                for (var i = 0; i < division; i++) {
                                    row.push(
                                        <svg aria-hidden="true" focusable="false" data-prefix="fas" data-icon="star" class="w-4 text-yellow-500 mr-1" role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 576 512">
                                          <path fill="currentColor" d="M259.3 17.8L194 150.2 47.9 171.5c-26.2 3.8-36.7 36.1-17.7 54.6l105.7 103-25 145.5c-4.5 26.3 23.2 46 46.4 33.7L288 439.6l130.7 68.7c23.2 12.2 50.9-7.4 46.4-33.7l-25-145.5 105.7-103c19-18.5 8.5-50.8-17.7-54.6L382 150.2 316.7 17.8c-11.7-23.6-45.6-23.9-57.4 0z"></path>
                                        </svg>
                                    );
                                }
                                return row;
                                })
                                }
                            </div>
                        </div>
                        {/* <div class="bg-gray-900 rounded-xl p-2 ml-4">
                            <p class="pb-2" >Oy Sayısı</p>
                            <p> {data.vote_count} </p>
                        </div>
                        <div class="bg-gray-900 rounded-xl p-2 ml-4">
                            <p class="pb-2">Vizyon Tarihi</p>
                            <p> {data.release_date} </p>
                        </div> */}
                        {/* <div class="bg-gray-900 rounded-xl p-2 ml-4">
                            <p class="pb-2">Tür</p>
                            <p>
                                {data.genres.map(data =>
                                    data.name 
                                )}
                            </p>
                        </div> */}
                    </div>
                    <p class="text-justify">{data.shortOverview}. </p>
                </div>
            </div>
            )}
        </div>
    </div>
  )}
