import React, {useContext} from 'react'
import CrewCard from './CrewCard'
import { DataContext } from '../Movies'

export default function CrewSection(props) {
    const data = useContext(DataContext)
    console.log(DataContext)
  return (
    <>
    <div className='flex flex-row gap-3'>
    <div>
    <p className='mb-3 text-center lg:text-left'>Yönetmen</p>
    <CrewCard data={props.data?.director[0]}/>
    </div>

    <div>
    {(props.data?.screenplay[0]) ? <p className='mb-3 text-center lg:text-left'>Senarist</p> : ''}
    {props.data?.screenplay.slice(0, 2).map(item =>
    <>
    <CrewCard data={item}/>
    </>
    )}
    </div>

    <div>
    {(props.data?.writer[0]) ? <p className='mb-3 text-center lg:text-left'>Yazar</p> : ''}
    {props.data?.writer.slice(0, 2).map(item =>
    <CrewCard data={item}/>
    )}    
    </div>
    </div>
    </>
  )
}
