import React, {useState, useEffect} from 'react'
import '../../src/css/output.css'
import { Carousel } from '@trendyol-js/react-carousel';


export default function MovieSlider() {
  const [arrays, setArray] = useState([]);
  useEffect( async ()=> {
    const response = await fetch("/movies/278");
    const data = await response.json();
    setArray(data.recommendedMovies.results);
},[])

 return (
    <div>MovieSlider

    <Carousel show={3.5} slide={3} swiping={true} className="bg-slate-800">

    <div className='bg-orange-200'>We love Trendyol orange</div>
    <div className='bg-teal-900'>We love Trendyol orange</div>
    <div className='bg-blue-700'>We love Trendyol orange</div>
    <div className='bg-yellow-400'>We love Trendyol orange</div>

    </Carousel>
 
    {
    arrays.map(array => console.log(array.id))
    }

    <div className="bg-slate-500 rounded-3xl w-4/6 m-auto sm:w-full flex ">
              {arrays.map(item =>
                  <li className="justify-start">
                      <img className=" ml-auto mr-auto rounded-xl sm:w-36 sm:mt-3 sm:mb-3" src={"https://www.themoviedb.org/t/p/w600_and_h900_bestv2"+ item.poster_path}/> 
                  </li> )}
        </div>

    </div>
        )
    }
