import React, { useEffect, useState } from "react";
import Header from "./components/Header";
import MovieSlider from "./components/MovieSlider";
import Sidebar from "./components/Sidebar";
import { useParams } from "react-router-dom";
import MovieMain from './components/MovieMain';
import ActorSection from "./components/ActorSection";
import CrewSection from "./components/CrewSection";

export const DataContext = React.createContext();

export default function Movies() {
    let { id } = useParams();
    const [arrays, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    useEffect(() => {
        setLoading(true);
        fetch("/movies/" + id)
          .then((res) => res.json())
          .then((data) => {
            setData(data);
          })
          .catch((err) => {
            console.log(err);
          })
          .finally(() => {
            setLoading(false);
          });
      }, []);
      console.log(arrays)
  if (loading) {

    return(            
            <>
                <Header/>
                <div className="h-screen">
                    <div className="flex flex-col justify-center bg-sky-900">
                        <div className=" w-40 h-40 mt-36 border-t-4 border-b-4 border-yellow-500 rounded-full animate-spin"></div>
                        <div>Loading</div>
                    </div>
                </div>
            </>
        )};

    return (
        <>
        <DataContext.Provider value={arrays}>
            <Header/>
            <div className="flex flex-wrap flex-col w-full sm:w-9/12 sm:m-auto pt-32 sm:pt-48">
            <MovieMain data={arrays}/>
            <ActorSection data={arrays.credits}/>
            </div>
            <MovieSlider movieId={id}/>
        </DataContext.Provider>
        </>
    )
}
