import React, {useState, useEffect} from 'react'

export default function MovieSlider() {

    const [list, setLists] = useState([]);

    useEffect(async () => {
      debugger;

      const response = await fetch("/movies/278");
      const data = await response.json();
      const item = data.recommendedMovies.results;
      setLists(item);
      list;

    }, []);

 return (
<div>MovieSlider
    <p class="text-center text-2xl p-8">{item}</p> 
    <div class="bg-slate-800 rounded-3xl w-4/6 m-auto sm:w-full flex ">
    <ul id="lightSlider" class="">
    {item.forEach(item => {
            <li class="justify-start">
            <img class=" ml-auto mr-auto rounded-xl sm:w-36 sm:mt-3 sm:mb-3" src="https://www.themoviedb.org/t/p/w600_and_h900_bestv2 ${item.poster_path}" /> 
        </li> 
    })}

    </ul>
    </div>
</div>
        )
    }
