import React, {useState, useEffect} from 'react'
import '../../src/dist/css/output.css'
import MovieCard from './MovieCard';

export default function MovieGrid(props) {
    const [arrays, setArray] = useState([]);
    useEffect( async ()=> {
    const response = await fetch("/main");
    const data = await response.json();
    console.log(data);
    setArray(data.data); //request tek object olarak geliyor(data[]) useState array olarak atanamıyor
  }, [])

  return (
    <div className="relative min-h-screen flex bg-gray-400">
      <div className="dark:bg-gray-800 w-2/12 min-h-screen" >
      </div>
      <div className="p-2 w-10/12">
        <div className="grid sm:grid-cols-2 md:grid-cols-4 lg:grid-cols-5 gap-4">
        {arrays.map(data =>
        <MovieCard  data={props.data} />
        )}
        
          </div>
        </div>
    </div>
)}
