export default function MovieSlider() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
  
    useEffect(() => {
      fetch("/movies/278")
        .then(res => res.json())
        .then(
          (result) => {
            setIsLoaded(true);
