import React, { useEffect, useState, useCallback } from "react";
import Header from "./components/Header";
import { QueryClient, QueryClientProvider, useQuery } from "react-query"
import MovieGrid from "./components/MovieGrid";
import CategoryBar from "./components/CategoryBar";
import { useParams } from "react-router-dom";
import Loader from "./components/Loader";

export default function HomePage() {
  
  const fetchPopular = async ()=> {
  const response = await fetch("/main");
  return response.json();

}
  const {data, status} = useQuery('popular', fetchPopular);
  console.log(data)

  const fetchGenre = async (genreId)=> {
    const response = await fetch(`movies/getMoviesByGenre/${genreId}`);
    return response.json();
  }
  const teamsQuery = useQuery('genre', fetchGenre(27))
  console.log(teamsQuery)

  // const {data, status} = useQuery('popular', fetchGenre);


  // const [genreId, setData] = useState(['']);

  // const getGenreId =  useCallback(async() => {
  //   const response = await fetch(`movies/getMoviesByGenre/${genreId}`);
  //   const data = await response.json();
  //   setData(data);
  // }, []);
  const [page, setPage] = useState('popular')

    return (
    <div className="bg-slate-300">
          <Header/>
          <CategoryBar/>
            {status === 'loading' && (
              <Loader/>
            )}
            {status === 'error' && (
              <div>Error fetching data</div>
            )}
            {status === 'success' && (
              <MovieGrid data={data.data}/>
            )}
            {(() => {
              switch (page) {
                case 'action':
                  return <ActionMovieGrid/>
                case 'comedy':
                  return <ComedyMovieGrid/>
                case 'drama':
                  return <DramaMovieGrid/>
                case 'horror':
                  return <HorrorMovieGrid/>
                case 'scifi':
                  return <SciFiMovieGrid/>
                case 'history':
                  return <HistoryMovieGrid/>
                default:
                  return null
                }
            })()}

          {/* <MovieGrid data={genreId}/> */}
    </div>
    )
}
