import React, {useState, useEffect} from 'react'
import '../../src/css/output.css'


export default function MovieGrid() {
    const [arrays, setArray] = useState([]);
    useEffect( async ()=> {
    const response = await fetch("/main");
    const data = await response.json();
    console.log(data);
    setArray(data.data);
  }, [])
  return (
    <div className="relative min-h-screen flex bg-themecolors-purple">
      <div className="dark:bg-gray-800 w-2/12 min-h-screen" >
      </div>
      <div className="p-2 w-10/12">
        <div className="grid sm:grid-cols-2 md:grid-cols-4 lg:grid-cols-5 gap-4">
        {arrays.map(data =>
        <div className="bg-themecolors-darkred rounded-b-2xl mb-8"> 
              <div className="text-center">
                <div className="p-4">
                  <img className="rounded-b-2xl" src={"https://www.themoviedb.org/t/p/w300_and_h450_bestv2" + data.poster_path} /> 
                </div>
                <a href={"/movies/" + data.id}/>
                <div className="flex pt-1 pb-4 justify-center">
                  <p> <i className="fa-solid fa-signal"></i> {data.vote_average} Puan</p>
                  <p className="pl-6"> <i className="fa-solid fa-star" ></i> {data.vote_count} Oy Sayısı</p>
                </div>
              </div>
        </div>
        )}
        
          </div>
        </div>
    </div>
)}
