import React, { useEffect, useState } from "react";
import Header from "./components/Header";
import MovieSlider from "./components/MovieSlider";
import Sidebar from "./components/Sidebar";
import { useParams } from "react-router-dom";
import MovieMain from './components/MovieMain';
import ActorBanner from "./components/ActorBanner";

export default function Movies() {
    let { id } = useParams();
    const [arrays, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    useEffect(() => {
        setLoading(true);
        fetch("/movies/" + id)
          .then((res) => res.json())
          .then((data) => {
            setData(data);
          })
          .catch((err) => {
            console.log(err);
          })
          .finally(() => {
            setLoading(false);
          });
      }, []);
  if (loading) {
    return <p>Data is loading...</p>;
  }

    return (
        <>
        <Header/>
        <div className="flex flex-wrap flex-col w-full sm:w-9/12 sm:m-auto pt-32 sm:pt-48">
        <MovieMain data={arrays.data}/>
        <ActorBanner data={arrays.credits}/>
        <MovieSlider movieId={id}/>
        </div>
        </>
    )
}
