import React, {useState, useEffect} from 'react'
import '../../src/css/output.css'

export default function MovieSlider() {
  const [arrays, setArray] = useState([]);
  useEffect( async ()=> {
    const response = await fetch("/movies/278");
    const data = await response.json();
    setArray(data.recommendedMovies.results);
},[])

useEffect(() => {
  const script = document.createElement('script');

  script.src = "https://cdnjs.cloudflare.com/ajax/libs/jquery/3.6.0/jquery.min.js";
  script.async = true;

  document.body.appendChild(script);

  return () => {
    document.body.removeChild(script);
  }
}, []);

useEffect(() => {
  const script = document.createElement('script');

  script.src = "https://cdnjs.cloudflare.com/ajax/libs/lightslider/1.1.6/js/lightslider.min.js";
  script.async = true;

  document.body.appendChild(script);

  return () => {
    document.body.removeChild(script);
  }
}, []);

 return (
    <div>MovieSlider
      <Carousel show={3.5} slide={3} swiping={true}>
    <Highlight color="#2d66c3">We love Web ğŸŒ</Highlight>
    <Highlight color="#f44336">We love Developers ğŸ‘©ğŸ»â€</Highlight>
    <a target="_blank" href="https://github.com/trendyol/">
        <Highlight color="#d53f8c">This is our github</Highlight>
    </a>
    <a target="_blank" href="https://trendyol.com/">
        <Highlight color="#f27a1a">This is our website</Highlight>
    </a>
    ...
    </Carousel>
            {
            arrays.map(array => console.log(array.id))
            }

    <div className="bg-slate-800 rounded-3xl w-4/6 m-auto sm:w-full flex ">
          <ul id="lightSlider">
              {arrays.map(item =>
                  <li className="justify-start">
                      <img className=" ml-auto mr-auto rounded-xl sm:w-36 sm:mt-3 sm:mb-3" src={"https://www.themoviedb.org/t/p/w600_and_h900_bestv2"+ item.poster_path}/> 
                  </li> )}
          </ul>
        </div>

    </div>
        )
    }
