import React, { useEffect, useState, useCallback } from "react";
import Header from "./components/Header";
import { QueryClient, QueryClientProvider, useQuery } from "react-query"
import MovieGrid from "./components/MovieGrid";
import CategoryBar from "./components/CategoryBar";
import { useParams } from "react-router-dom";
import Loader from "./components/Loader";

export default function HomePage() {
  
  const fetchPopular = async ()=> {
  const response = await fetch("/main");
  return response.json();

}
  const {data, status} = useQuery('popular', fetchPopular);
  console.log(data)
  const getGenreId = (id) => {
    const fetchGenre = async (getGenreId)=> {
      const response = await fetch(`movies/getMoviesByGenre/${getGenreId}`);
      return response.json();
    }
    const teamsQuery = useQuery('genre',  () => fetchGenre(27))
  };
  const fetchGenre = async (getGenreId)=> {
    const response = await fetch(`movies/getMoviesByGenre/${getGenreId}`);
    return response.json();
  }
  const teamsQuery = useQuery('genre',  () => fetchGenre(27))
  console.log(teamsQuery)

  // const {data, status} = useQuery('popular', fetchGenre);


  // const [genreId, setData] = useState(['']);

    return (
    <div className="bg-slate-300">
          <Header/>
          <CategoryBar {...getGenreId()}/>
            {status === 'loading' && (
              <Loader/>
            )}
            {status === 'error' && (
              <div>Error fetching data</div>
            )}
            {status === 'success' && (
              <MovieGrid data={data.data}/>
            )}
          {/* <MovieGrid data={genreId}/> */}
    </div>
    )
}
