import React from 'react'

export default function MovieGrid() {
    const [arrays, setArray] = useState([]);
    useEffect( async ()=> {
      const response = await fetch("/");
      const data = await response.json();
      setArray(data);
  },[])
  return (
    <div class="relative min-h-screen flex bg-themecolors-purple">
      <div class="dark:bg-gray-800 w-2/12 min-h-screen" >
      </div>
      <div class="p-2 w-10/12">
        <div class="grid sm:grid-cols-2 md:grid-cols-4 lg:grid-cols-5 gap-4">
        {arrays.map(data =>
        <div class="bg-themecolors-darkred rounded-b-2xl mb-8"> 
              <div class="text-center">
                <div class="p-4">
                  <img class="rounded-b-2xl" src={"https://www.themoviedb.org/t/p/w300_and_h450_bestv2" + data.poster_path} /> 
                </div>
                <a href={"/movies/" + data.id}/>
                <div class="flex pt-1 pb-4 justify-center">
                  <p> <i class="fa-solid fa-signal"></i> {data.vote_average} Puan</p>
                  <p class="pl-6"> <i class="fa-solid fa-star" ></i> {data.vote_count} Oy Sayısı</p>
                </div>
              </div>
        </div>
        )}
        
          </div>
        </div>
    </div>
)}
